catalog_script_client.script:
function onLoad() {
	// default values do not work for "custom" variables
	var oAuthData = JSON.parse(g_service_catalog.parent.getValue('oauth_data'));
	g_form.setValue('mpl_instance_oauth_state', oAuthData.state);

	var url = g_form.getValue('mpl_instance_url');
	if (url) {
		g_form.setValue('mpl_instance_oauth_token', oAuthData.tokens[url]);
	}

	NOW.mpl_instance_onUpdated = NOW.mpl_instance_onUpdated || [];
	NOW.mpl_instance_variables = {
		connect_button: {
			disabled: true
		},
		instructions_script: {
			disabled: true
		}
	};

	NOW.mpl_instance_changed = function (changedField) {
		var instanceURL = g_form.getValue('mpl_instance_url');
		var buttonDisabled = true;
		var connected = false;
		try {
			new URL(instanceURL);
			connected = !!g_form.getValue('mpl_instance_oauth_token');
			// the connect button is enabled if we are not yet connected and we have a valid URL
			buttonDisabled = connected ? true : false;
		} catch (e) {

		}

		NOW.mpl_instance_variables.instructions_script.disabled = buttonDisabled;
		NOW.mpl_instance_variables.connect_button.disabled = buttonDisabled;
		NOW.mpl_instance_onUpdated.forEach(function (f) {
			try {
				f(g_form);
			} catch (e) {
				var errorMessage = 'Error: ' + e;
				if (e.stack) {
					errorMessage += '\n' + e.stack;
				}

				jslog(errorMessage, 'mpl_instance');
			}
		});
	};
}
