sys_variable_value.value:
(function execute(inputs, outputs) {

  // when the access token grants e.g. repo access, but we only need public_repo,
  // the "test github access" check should be performed with the scope public_repo.
  // GitHub will return the repo scope, but as public_repo is included there this
  // needs to be checked alternatively to pure-public_repo.
  const scopeMap = {
    // repo
    'repo:status': 'repo',
    'repo_deployment': 'repo',
    'public_repo': 'repo',
    'repo:invite': 'repo',
    'security_events': 'repo',
    // admin:org
    'write:org': 'admin:org',
    'read:org': 'admin:org',
    // admin:public_key
    'write:public_key': 'admin:public_key',
    'read:public_key': 'admin:public_key',
    // admin:repo_hook
    'write:repo_hook': 'admin:repo_hook',
    'read:repo_hooh': 'admin:repo_hook',
    // user    
    'read:user': 'user',
    'user:email': 'user',
    'user:follow': 'user',
    // write:discussion
    'read:discussion': 'write:discussion',
    // adminn:enterprise
    'manage_runners:enterprise': 'admin:enterprise',
    'manage_billing:enterprise': 'admin:enterprise',
    'read:enterprise': 'admin:enterprise',
    // audit_log
    'read:audit_log': 'audit_log',
    // project
    'read:project': 'project',
    // admin:gpg_key
    'write:gpg_key': 'admin:gpg_key',
    'read:gpg_key': 'admin:gpg_key',
    // admin:ssh_signing_key
    'write:ssh_signing_key': 'admin:ssh_signing_key',
    'read:ssh_signing_key': 'admin:ssh_signing_key'
  };

  try {
    const headers = JSON.parse(inputs.headers);
    if (headers['X-OAuth-Scopes']) {
      const authorizedScopes = headers['X-OAuth-Scopes'][0].split(',').map(s => s.trim());
      if (authorizedScopes.length) {
        outputs.scopes = authorizedScopes;
      } else {
        outputs.scopes = null;
      }

      outputs.has_access = inputs.expected_scopes.every(x => authorizedScopes.includes(x) || (x in scopeMap && authorizedScopes.includes(scopeMap[x])));
    }
  } catch (e) {

  }

})(inputs, outputs);

