sys_variable_value.value:
(function execute(inputs, outputs) {

    const result = JSON.parse(inputs.body);
    const version = result && result.result && result.result.length && result.result[0].value;
    if (version) {
        const versionMatch = version.match(/glide-(.*?)-\d{2}-\d{2}-\d{4}__(.*?)-\d{2}-\d{2}-\d{4}_\d{2}-\d{2}-\d{4}_\d+.zip/);
        if (versionMatch) {
            outputs.major_version = versionMatch[1];
            outputs.minor_version = versionMatch[2];
        }
    }

})(inputs, outputs);

