sys_variable_value.value:
(function execute(inputs, outputs) {

  // Several Bugs with the Flow Designer:
  // 1.) REST Step Form URL Encoded doesn't encode its values...
  // 2.) Even though it is explictily stated to decrypt Password 2 values, this doesn't work properly
  //     (the values are still transmitted as GLIDE_FDIH_P2[<decrypted value>]
  outputs.client_secret_url_encoded = encodeURIComponent(inputs.oauth_token_record.instance.customer.oauth_client_secret.getDecryptedValue());

  const refreshToken = inputs.oauth_token_record.refresh_token.getDecryptedValue();
  outputs.refresh_token_url_encoded = encodeURIComponent(refreshToken);
  outputs.original_access_token = inputs.oauth_token_record.access_token.getDecryptedValue();
  outputs.original_refresh_token = refreshToken;
  outputs.original_expiration_date = inputs.oauth_token_record.expires_at;

})(inputs, outputs);

