sys_variable_value.value:
(function execute(inputs, outputs) {

  try {
   let tokenData = JSON.parse(inputs.response_body);
    outputs.access_token = tokenData.access_token;
    outputs.refresh_token = tokenData.refresh_token;

    let expiresAt = new GlideDateTime();
    expiresAt.addSeconds(tokenData.expires_in);
    outputs.expires_at = expiresAt;
    outputs.has_error = false;
  } catch (e) {
    outputs.access_token = inputs.original_access_token;
    outputs.refresh_token = inputs.original_refresh_token;
    outputs.expires_at = inputs.original_expiration_date;
    outputs.has_error = true;
  }


})(inputs, outputs);

