sys_variable_value.value:
(function execute(inputs, outputs) {
  const result = JSON.parse(inputs.response_body).result;
  if (result.length) {
    outputs.record_sys_id = result[0].sys_id.value;
    outputs.repo_url = result[0].url.value;
    // no display for the app indicates that a sys_repo_config record exists,
    // but the application is currently not installed under the given query
    outputs.app_installed = !!result[0].sys_app.display_value;
  }
})(inputs, outputs);

