sys_variable_value.value:
(function execute(inputs, outputs) {

  try {
    const body = JSON.parse(inputs.response_body);
    if (GlideStringUtil.isBase64(body.content)) {
      const content = gs.base64Decode(body.content);
      const pathMatch = content.match(/^path=(.+)/m);
      if (pathMatch) {
        outputs.app_folder = pathMatch[1];
      }
    }
  } catch (e) {
    // No need for error handling here:
    // outputs.app_folder will be empty and trigger the 'Error Evaluation'
  }

})(inputs, outputs);

