sys_variable_value.value:
(function execute(inputs, outputs) {

  try {
    const body = JSON.parse(inputs.response_body);
    if (GlideStringUtil.isBase64(body.content)) {
      const xml = new XMLDocument2();
      xml.parseXML(gs.base64Decode(body.content));
      outputs.scope_name = xml.getNodeText('//sys_app/scope');
      outputs.scope_sys_id = xml.getNodeText('//sys_app/sys_id');
    }
  } catch (e) {
    // No need for error handling here:
    // outputs.app_folder will be empty and trigger the 'Error Evaluation'
  }

})(inputs, outputs);

