sys_variable_value.value:
(function execute(inputs, outputs) {

  try {
    const files = JSON.parse(inputs.response_body);
    for (let i = 0; i < files.length; i++) {
      const fileName = files[i].name;
      if (fileName.startsWith('sys_app_') && fileName.endsWith('.xml')) {
        outputs.app_file = fileName;
        break;
      }
    }
  } catch (e) {
    // No need for error handling here as it takes place in 'Error Evaluation'
  }

})(inputs, outputs);

