sys_ui_action.script:
/* global GlideModal, g_form, g_navigation, getMessage, GlideAjax */
/* eslint no-undef: "error" */
function deployApp() {
	var ga = new GlideAjax('x_376096_mpl.MPLUtilClient');
	ga.addParam('sysparm_name', 'deployApp');
	ga.addParam('customer', g_form.getValue('customer'));
	ga.addParam('application', g_form.getValue('application'));
	ga.addParam('automatic_update', g_form.getValue('automatic_update'));
	ga.addParam('service_user', g_form.getValue('service_user'));
	ga.getXMLAnswer(function (result) {
		result = JSON.parse(result);
		if (result.status == 'success') {
			var trackerId = result.tracker_id;
			var dd = new GlideModal('hierarchical_progress_viewer', false, '40em', '10em');
			dd.setTitle(getMessage('Status'));
			dd.setPreference('sysparm_renderer_execution_id', trackerId);
			dd.on('executionComplete', function (result) {
				if (result && result.result && result.result.application_installation) {
					g_form.modified = false;
					g_navigation.openRecord('x_376096_mpl_application_installation', result.result.application_installation);
				}
			});
			dd.render();
		} else if (result.message) {
			g_form.addErrorMessage(result.message);
		}
	});
}
