sysevent_script_action.script:
/* global current, event, GlideRecord, sn_fd */
/* eslint no-undef: "error" */
(function (appGr, eventGr) {

	const updateHandlerCategorySysID = '1882cc8397a32510d70ef207f053afd1';
	const appInstallGr = new GlideRecord('x_376096_mpl_app_installation');
	appInstallGr.addQuery('active', true);
	appInstallGr.addQuery('application', appGr.sys_id);
	appInstallGr.addQuery('update_flow.active', true);
	appInstallGr.addQuery('update_flow.category', updateHandlerCategorySysID);
	appInstallGr.addQuery('service_user.active', true);
	appInstallGr.query();
	while (appInstallGr.next()) {
		const serviceUserGr = appInstallGr.service_user.getRefRecord();
		sn_fd.FlowAPI.getRunner()
			.subflow(appInstallGr.update_flow.internal_name)
			.inBackground()
			.withInputs({ 'application_installation': appInstallGr, 'user': serviceUserGr })
			.run();
	}

})(current, event);
